import React from 'react';

function TaskList({ tasks, onEdit, onDelete, onToggleComplete }) {
  if (!tasks || tasks.length === 0) return <p>No tasks yet!</p>;

  return (
    <div>
      {tasks.map(task => (
        <div key={task.id} style={{ border: '1px solid #ccc', margin: '5px', padding: '10px' }}>
          <h3>
            {task.title} {task.completed ? '✅' : '❌'}
          </h3>
          <p>{task.description}</p>
          <button onClick={() => onToggleComplete(task)}>Toggle Complete</button>
          <button onClick={() => onEdit(task)}>Edit</button>
          <button onClick={() => onDelete(task.id)}>Delete</button>
        </div>
      ))}
    </div>
  );
}

export default TaskList;
